import React, { useState, useEffect, useRef } from "react";

export default function App() {
  const [sessionId] = useState(() => Date.now().toString());
  const [msgs, setMsgs] = useState([
    { from: "bot", text: "Hello! Iâ€™m your Credit Card Advisor. Letâ€™s start with your profile." },
  ]);
  const [input, setInput] = useState("");
  const bottomRef = useRef();

  // Auto-scroll when new messages arrive
  useEffect(() => {
    bottomRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [msgs]);

  // Enforce numeric-only if last bot question contains â€œ%â€
  const lastBot = [...msgs].reverse().find(m => m.from === "bot");
  const numericOnly = lastBot?.text.includes("%");

  const sendMessage = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    // 1) Echo user text
    setMsgs(m => [...m, { from: "user", text: input }]);
    const text = input;
    setInput("");

    // 2) Send to backend
    const res = await fetch("http://localhost:8000/chat", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ session_id: sessionId, message: text }),
    });
    const data = await res.json();

    // 3a) If recommendations, inject each as plainâ€text chat bubbles
    if (data.recommendations) {
      const recMsgs = data.recommendations.map(c => {
        const rewards = Object.entries(c.rewards)
          .map(([k, v]) => `${k.replace(/_/g," ")} ${v}%`)
          .join(", ");
        const perks = c.perks.join(", ");
        return {
          from: "bot",
          text:
            `ðŸ·ï¸ ${c.name}\n` +
            `Fee: â‚¹${c.annual_fee}/yr\n` +
            `Rewards: ${rewards}\n` +
            `Perks: ${perks}\n` +
            `Reason: ${computeReason(c.rewards)}\n`
        };
      });
      setMsgs(m => [...m, ...recMsgs]);
      return;
    }

    // 3b) Otherwise show next question or reâ€ask if validation failed
    if (data.reply) {
      setMsgs(m => [...m, { from: "bot", text: data.reply }]);
    }
  };

  // Derive a friendly reason string from the rewards object
  const computeReason = (rewards) => {
    let best = ["",0];
    for (const [k,v] of Object.entries(rewards)) {
      if (v > best[1]) best = [k,v];
    }
    const [k,v] = best;
    if (k.includes("travel_miles"))      return `Excellent travel miles rate of ${v}%`;
    if (k.includes("grocery_cashback"))  return `Great grocery cashback of ${v}%`;
    if (k.includes("dining_cashback"))   return `High dining cashback of ${v}%`;
    if (k.includes("fuel_cashback"))     return `Super fuel cashback of ${v}%`;
    if (k.includes("all_spends_cashback")) return `Flat cashback on all spends at ${v}%`;
    return `Top reward rate: ${v}%`;
  };

  return (
    <div className="h-screen flex flex-col bg-gray-100">
      {/* Chat history */}
      <div className="flex-1 overflow-y-auto p-4 flex flex-col gap-2 max-w-xl mx-auto">
        {msgs.map((m,i) => (
          <div
            key={i}
            className={`flex w-full ${m.from==="bot"?"justify-start":"justify-end"}`}
          >
            <pre className={`
              whitespace-pre-wrap p-3 rounded-lg border shadow
              ${m.from==="bot"?"bg-white":"bg-blue-500 text-white"}
              max-w-xs
            `}>
              {m.text}
            </pre>
          </div>
        ))}
        <div ref={bottomRef} />
      </div>

      {/* Always-visible input */}
      <form onSubmit={sendMessage} className="p-4 flex gap-2 max-w-xl mx-auto">
        <input
          type={numericOnly?"number":"text"}
          inputMode={numericOnly?"numeric":"text"}
          pattern={numericOnly?"[0-9]*":undefined}
          className="flex-grow p-2 border rounded"
          value={input}
          onChange={e => setInput(e.target.value)}
          placeholder="Type your answerâ€¦"
        />
        <button className="bg-blue-600 text-white px-4 py-2 rounded">
          Send
        </button>
      </form>
    </div>
  );
}
