import React, { useState } from 'react';

function App() {
  const [message, setMessage] = useState('');
  const [chat, setChat] = useState([]);

  const sendMessage = async () => {
    if (!message) return;
    const userMsg = { sender: 'user', text: message };
    setChat([...chat, userMsg]);
    setMessage('');
    const response = await fetch('http://localhost:8000/chat', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ message: userMsg.text }),
    });
    const data = await response.json();
    setChat(prev => [...prev, { sender: 'bot', text: data.reply }]);
  };

  return (
    <div className="flex flex-col items-center p-4">
      <h1 className="text-2xl mb-4">Credit Card Advisor</h1>
      <div className="w-full max-w-md border p-4 h-[400px] overflow-y-auto mb-4">
        {chat.map((msg, idx) => (
          <div key={idx} className={msg.sender === 'user' ? 'text-right' : 'text-left'}>
            <p className="inline-block p-2 my-1 rounded bg-gray-200">{msg.text}</p>
          </div>
        ))}
      </div>
      <div className="flex w-full max-w-md">
        <input
          className="flex-grow border p-2"
          value={message}
          onChange={e => setMessage(e.target.value)}
          onKeyDown={e => e.key === 'Enter' && sendMessage()}
        />
        <button className="ml-2 p-2 bg-blue-500 text-white" onClick={sendMessage}>
          Send
        </button>
      </div>
    </div>
  );
}

export default App;
