// frontend/src/App.js
import React, { useState, useEffect, useRef } from "react";

/**
 * Renders a single recommended card as a chat bubble,
 * computing a human-friendly Reason based on the highest reward rate.
 */
function CardBubble({ card }) {
  // Find the reward type with the highest percentage
  const entries = Object.entries(card.rewards || {});
  const [bestKey, bestVal] = entries.reduce(
    (max, curr) => (curr[1] > max[1] ? curr : max),
    entries[0] || ["", 0]
  );

  // Map reward key to a readable label
  const reasonText = (() => {
    if (bestKey.includes("travel_miles")) return `Strong travel miles rate of ${bestVal}%`;
    if (bestKey.includes("grocery_cashback")) return `Great grocery cashback of ${bestVal}%`;
    if (bestKey.includes("dining_cashback")) return `Excellent dining cashback of ${bestVal}%`;
    if (bestKey.includes("fuel_cashback"))   return `High fuel cashback of ${bestVal}%`;
    if (bestKey.includes("all_spends_cashback")) return `Flat cashback on all spends at ${bestVal}%`;
    return `Reward rate of ${bestVal}%`;
  })();

  return (
    <div className="self-start border rounded-lg bg-white p-4 shadow-md max-w-xl my-2">
      {card.image_url && (
        <img
          src={card.image_url}
          alt={card.name}
          className="w-full h-32 object-cover rounded mb-2"
        />
      )}
      <h3 className="font-bold text-lg mb-1">{card.name}</h3>
      {card.fee != null && (
        <p className="text-xs text-gray-500 mb-1">Fee: ₹{card.fee}/yr</p>
      )}
      {card.rewards && (
        <p className="text-sm mb-1">
          <strong>Rewards:</strong>{" "}
          {entries
            .map(([k, v]) => `${k.replace(/_/g, " ")} ${v}%`)
            .join(", ")}
        </p>
      )}
      {card.perks && (
        <p className="text-sm mb-1">
          <strong>Perks:</strong> {card.perks.join(", ")}
        </p>
      )}
      {/* Computed Reason */}
      <p className="mt-2 text-gray-700">
        <strong>Reason:</strong> {reasonText}
      </p>
      {card.reward_simulation && (
        <p className="mt-1 font-semibold text-gray-800">
          {card.reward_simulation}
        </p>
      )}
    </div>
  );
}

export default function App() {
  const [sessionId] = useState(() => Date.now().toString());
  const [msgs, setMsgs] = useState([
    {
      from: "bot",
      text:
        "Hello! I’m your Credit Card Advisor. Let’s start with your profile.",
    },
  ]);
  const [input, setInput] = useState("");
  const [cards, setCards] = useState(null);
  const bottomRef = useRef();

  // Auto-scroll whenever chat or cards change
  useEffect(() => {
    bottomRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [msgs, cards]);

  // If last bot message asked “%”, restrict input to numbers
  const lastBot = [...msgs].reverse().find((m) => m.from === "bot");
  const numericOnly = lastBot?.text.includes("%");

  const sendMessage = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    // 1) Echo user message
    setMsgs((m) => [...m, { from: "user", text: input }]);
    const userText = input;
    setInput("");

    // 2) Call backend
    const res = await fetch("http://localhost:8000/chat", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ session_id: sessionId, message: userText }),
    });
    if (!res.ok) {
      setMsgs((m) => [
        ...m,
        { from: "bot", text: "Oops, something went wrong. Please try again." },
      ]);
      return;
    }
    const data = await res.json();

    // 3) If we got recommendations, show them (and keep chat open)
    if (data.recommendations) {
      setCards(data.recommendations);
      return;
    }

    // 4) Otherwise display next question or validation error
    if (data.reply) {
      setMsgs((m) => [...m, { from: "bot", text: data.reply }]);
    } else {
      setMsgs((m) => [
        ...m,
        { from: "bot", text: "Sorry, I didn’t understand that. Please try again." },
      ]);
    }
  };

  return (
    <div className="h-screen flex flex-col bg-gray-50">
      {/* Chat history */}
      <div className="flex-1 overflow-y-auto p-4 flex flex-col items-start max-w-xl mx-auto">
        {msgs.map((m, i) => (
          <div
            key={i}
            className={`w-full my-2 flex ${
              m.from === "bot" ? "justify-start" : "justify-end"
            }`}
          >
            <span
              className={`inline-block p-3 rounded whitespace-pre-wrap ${
                m.from === "bot"
                  ? "bg-white shadow max-w-xl"
                  : "bg-blue-500 text-white max-w-xs"
              }`}
            >
              {m.text}
            </span>
          </div>
        ))}

        {/* Recommendations inline */}
        {cards && cards.map((card) => <CardBubble key={card.name} card={card} />)}

        <div ref={bottomRef} />
      </div>

      {/* Always-visible input */}
      <form
        onSubmit={sendMessage}
        className="p-4 max-w-xl mx-auto flex gap-2"
      >
        <input
          type={numericOnly ? "number" : "text"}
          inputMode={numericOnly ? "numeric" : "text"}
          pattern={numericOnly ? "[0-9]*" : undefined}
          className="flex-grow p-2 border rounded"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Type your answer…"
        />
        <button className="bg-blue-600 text-white px-4 py-2 rounded">
          Send
        </button>
      </form>
    </div>
  );
}
