import React, { useState, useEffect, useRef } from "react";

// Renders a single card as a chat bubble
function CardBubble({ card }) {
  return (
    <div className="border rounded-lg bg-white p-4 shadow-md max-w-xl my-2 self-start">
      {card.image_url && (
        <img
          src={card.image_url}
          alt={card.name}
          className="w-full h-32 object-cover rounded mb-2"
        />
      )}
      <h3 className="font-bold text-lg">{card.name}</h3>
      <p className="text-xs text-gray-500 mb-1">Fee: ₹{card.fee}/yr</p>
      <p className="text-sm mb-1">
        <strong>Rewards:</strong>{" "}
        {Object.entries(card.rewards)
          .map(([k, v]) => `${k.replace(/_/g, " ")} ${v}%`)
          .join(", ")}
      </p>
      <p className="text-sm mb-1">
        <strong>Perks:</strong> {card.perks.join(", ")}
      </p>
      <p className="mt-2 italic text-gray-700">
        <strong>Reason:</strong> {card.reason}
      </p>
      <p className="mt-1 font-semibold text-gray-800">
        {card.reward_simulation}
      </p>
    </div>
  );
}

function App() {
  const [sessionId] = useState(() => Date.now().toString());
  const [msgs, setMsgs] = useState([
    {
      from: "bot",
      text: "Hello! I’m your Credit Card Advisor. Let’s start with your profile.",
    },
  ]);
  const [input, setInput] = useState("");
  const [cards, setCards] = useState(null);
  const bottomRef = useRef();

  // Auto-scroll on new message or when cards arrive
  useEffect(() => {
    bottomRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [msgs, cards]);

  // Detect if last bot message asks for a percentage
  const lastBot = [...msgs].reverse().find((m) => m.from === "bot");
  const numericOnly = lastBot?.text.includes("%");

  const sendMessage = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    // 1) display the user's message
    setMsgs((m) => [...m, { from: "user", text: input }]);
    const text = input;
    setInput("");

    // 2) call your FastAPI backend
    const res = await fetch("http://localhost:8000/chat", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ session_id: sessionId, message: text }),
    });

    if (!res.ok) {
      setMsgs((m) => [
        ...m,
        { from: "bot", text: "Oops, something went wrong. Try again." },
      ]);
      return;
    }

    const data = await res.json();

    // 3a) If recommendations arrived, render them and stop the chat
    if (data.recommendations) {
      setCards(data.recommendations);
      return;
    }

    // 3b) Otherwise, show the next question or error
    if (data.reply) {
      setMsgs((m) => [...m, { from: "bot", text: data.reply }]);
    } else {
      setMsgs((m) => [
        ...m,
        { from: "bot", text: "Unexpected response format." },
      ]);
    }
  };

  return (
    <div className="h-screen flex flex-col bg-gray-50">
      <div className="flex-1 overflow-y-auto p-4 flex flex-col items-start max-w-xl mx-auto">
        {msgs.map((m, i) => (
          <div
            key={i}
            className={`w-full my-2 flex ${
              m.from === "bot" ? "justify-start" : "justify-end"
            }`}
          >
            <span
              className={`inline-block p-3 rounded whitespace-pre-wrap ${
                m.from === "bot"
                  ? "bg-white shadow"
                  : "bg-blue-500 text-white"
              } max-w-xs`}
            >
              {m.text}
            </span>
          </div>
        ))}

        {/* 4) Inject card bubbles right here */}
        {cards &&
          cards.map((c) => <CardBubble key={c.name} card={c} />)}

        <div ref={bottomRef} />
      </div>

      {/* Hide input once cards are displayed */}
      {!cards && (
        <form
          onSubmit={sendMessage}
          className="p-4 max-w-xl mx-auto flex gap-2"
        >
          <input
            type={numericOnly ? "number" : "text"}
            inputMode={numericOnly ? "numeric" : "text"}
            pattern={numericOnly ? "[0-9]*" : undefined}
            className="flex-grow p-2 border rounded"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            placeholder="Type your answer…"
          />
          <button className="bg-blue-600 text-white px-4 py-2 rounded">
            Send
          </button>
        </form>
      )}
    </div>
  );
}

export default App;
