import React, { useState, useEffect, useRef } from "react";

// Card component (used in summary & compare)
function Card({ card, selectable, selected, onToggle }) {
  return (
    <div className="border rounded-lg p-4 shadow-sm relative">
      {selectable && (
        <input
          type="checkbox"
          className="absolute top-2 left-2"
          checked={selected}
          onChange={() => onToggle(card.name)}
        />
      )}
      {card.image_url && (
        <img
          src={card.image_url}
          alt={card.name}
          className="w-full h-32 object-cover rounded mb-2"
        />
      )}
      <h3 className="font-bold text-lg">{card.name}</h3>
      <p className="text-xs text-gray-500 mb-1">Fee: ₹{card.fee}/yr</p>
      <p className="text-sm mb-1">
        Rewards:{" "}
        {Object.entries(card.rewards)
          .map(([k, v]) => `${k.replace(/_/g, " ")} ${v}%`)
          .join(", ")}
      </p>
      <p className="text-sm mb-1">Perks: {card.perks.join(", ")}</p>
      <p className="mt-2 italic text-gray-700">{card.reason}</p>
      <p className="mt-1 font-semibold text-gray-800">{card.reward_simulation}</p>
    </div>
  );
}

function App() {
  const [sessionId] = useState(() => Date.now().toString());
  const [msgs, setMsgs] = useState([
    { from: "bot", text: "Hello! I'm your Credit Card Advisor." },
  ]);
  const [input, setInput] = useState("");
  const [finished, setFinished] = useState(false);
  const [cards, setCards] = useState([]);
  const bottomRef = useRef();

  useEffect(() => bottomRef.current?.scrollIntoView({ behavior: "smooth" }), [msgs]);

  const sendMessage = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;
    setMsgs((m) => [...m, { from: "user", text: input }]);
    const txt = input;
    setInput("");

    const res = await fetch("http://localhost:8000/chat", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ session_id: sessionId, message: txt }),
    });
    const data = await res.json();

    if (data.recommendations) {
      setCards(data.recommendations);
      setFinished(true);
    } else if (data.reply) {
      setMsgs((m) => [...m, { from: "bot", text: data.reply }]);
    } else {
      setMsgs((m) => [...m, { from: "bot", text: "Unexpected response." }]);
    }
  };

  if (finished) {
    return <Summary cards={cards} onRestart={() => {
      setMsgs([{ from: "bot", text: "Hello! I'm your Credit Card Advisor." }]);
      setFinished(false);
      setCards([]);
      setInput("");
    }} />;
  }

  return (
    <div className="h-screen flex flex-col bg-gray-50">
      <div className="flex-1 overflow-y-auto p-4 max-w-xl mx-auto">
        {msgs.map((m, i) => (
          <div key={i} className={`my-2 ${m.from==="bot"?"text-left":"text-right"}`}>
            <span className={`inline-block p-2 rounded ${
                m.from==="bot"?"bg-white shadow":"bg-blue-500 text-white"
              }`}>
              {m.text}
            </span>
          </div>
        ))}
        <div ref={bottomRef} />
      </div>

      <form onSubmit={sendMessage} className="p-4 max-w-xl mx-auto flex gap-2">
        <input
          className="flex-grow p-2 border rounded"
          value={input}
          onChange={e => setInput(e.target.value)}
          placeholder="Type your answer…"
        />
        <button className="bg-blue-600 text-white px-4 py-2 rounded">Send</button>
      </form>
    </div>
  );
}

function Summary({ cards, onRestart }) {
  const [selected, setSelected] = useState([]);

  const toggle = (name) =>
    setSelected((s) =>
      s.includes(name) ? s.filter((x) => x !== name) : [...s, name]
    );

  const toCompare = cards.filter((c) => selected.includes(c.name));

  return (
    <div className="h-screen p-4 bg-gray-50">
      <div className="max-w-xl mx-auto">
        <h2 className="text-2xl mb-4">Your Top Picks</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
          {cards.map((c) => (
            <Card
              key={c.name}
              card={c}
              selectable
              selected={selected.includes(c.name)}
              onToggle={toggle}
            />
          ))}
        </div>

        <div className="mt-6 flex items-center gap-4">
          <button
            disabled={toCompare.length < 2}
            onClick={() => {}}
            className={`px-4 py-2 rounded ${
              toCompare.length >= 2
                ? "bg-green-500 text-white"
                : "bg-gray-300 text-gray-600 cursor-not-allowed"
            }`}
          >
            Compare Selected
          </button>
          <button onClick={onRestart} className="underline text-blue-600">
            Restart Chat
          </button>
        </div>

        {toCompare.length >= 2 && <Compare cards={toCompare.slice(0,2)} />}
      </div>
    </div>
  );
}

function Compare({ cards }) {
  return (
    <div className="mt-8">
      <h3 className="text-xl mb-4">Comparison</h3>
      <div className="grid grid-cols-2 gap-4">
        {cards.map((c) => (
          <div key={c.name} className="p-4 border rounded bg-white shadow-sm">
            <h4 className="font-bold mb-2">{c.name}</h4>
            <p><strong>Fee:</strong> ₹{c.fee}/yr</p>
            <p><strong>Rewards:</strong>{" "}
              {Object.entries(c.rewards)
                .map(([k,v]) => `${k.replace(/_/g," ")} ${v}%`)
                .join(", ")}
            </p>
            <p><strong>Perks:</strong> {c.perks.join(", ")}</p>
            <p className="mt-2 italic">{c.reward_simulation}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
