// frontend/src/App.js
import React, { useState, useEffect, useRef } from "react";

/**
 * Parses a final reply string of the form:
 *  Here are some cards you might like:
 *  - Name (1234₹ fee): reward1, reward2. Perks: perk1, perk2.
 *  - ...
 *
 * into an array of { name, fee, rewards, perks } objects.
 */
function parseCards(reply) {
  const lines = reply.split("\n").map(l => l.trim());
  // drop the first line, keep only the "- " lines
  return lines
    .filter(l => l.startsWith("- "))
    .map(l => {
      // - Name (1000₹ fee): rew. Perks: p1, p2.
      const re = /^-\s+(.*?)\s+\((\d+)₹ fee\):\s*(.*?)\.\s*Perks:\s*(.*?)\.?$/;
      const m = l.match(re);
      if (!m) return null;
      return {
        name:    m[1],
        fee:     parseInt(m[2], 10),
        rewards: m[3],
        perks:   m[4],
      };
    })
    .filter(Boolean);
}

function App() {
  const [sessionId] = useState(() => Date.now().toString());
  const [msgs, setMsgs]   = useState([
    { from: "bot", text: "Hello! I'm your Credit Card Advisor." }
  ]);
  const [input, setInput] = useState("");
  const [finished, setFinished] = useState(false);
  const [cards, setCards]       = useState([]);
  const bottomRef = useRef();

  // auto‐scroll
  useEffect(() => bottomRef.current?.scrollIntoView({ behavior: "smooth" }), [msgs]);

  const sendMessage = async e => {
    e.preventDefault();
    if (!input.trim()) return;

    // 1) echo user
    setMsgs(m => [...m, { from: "user", text: input }]);
    const text = input;
    setInput("");

    // 2) call backend
    const res = await fetch("http://localhost:8000/chat", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ session_id: sessionId, message: text })
    });
    if (!res.ok) {
      setMsgs(m => [...m, { from: "bot", text: "Oops, something went wrong." }]);
      return;
    }
    const { reply } = await res.json();

    // 3) display bot reply
    setMsgs(m => [...m, { from: "bot", text: reply }]);

    // 4) if this is the final reply, parse cards & switch to summary
    if (reply.startsWith("Here are some cards you might like:")) {
      const parsed = parseCards(reply);
      setCards(parsed);
      setFinished(true);
    }
  };

  // If finished, show Summary instead of chat
  if (finished) {
    return (
      <Summary
        cards={cards}
        onRestart={() => {
          // reset everything
          setMsgs([{ from: "bot", text: "Hello! I'm your Credit Card Advisor." }]);
          setFinished(false);
          setCards([]);
        }}
      />
    );
  }

  // Otherwise show chat UI
  return (
    <div className="h-screen flex flex-col max-w-xl mx-auto p-4">
      <div className="flex-1 overflow-y-auto bg-white p-4 rounded shadow">
        {msgs.map((m,i) => (
          <div key={i} className={`my-2 ${m.from==="bot"?"text-left":"text-right"}`}>
            <span
              className={`inline-block p-2 rounded whitespace-pre-wrap ${
                m.from==="bot" ? "bg-gray-200" : "bg-blue-500 text-white"
              }`}
            >
              {m.text}
            </span>
          </div>
        ))}
        <div ref={bottomRef} />
      </div>
      <form onSubmit={sendMessage} className="mt-2 flex">
        <input
          className="flex-1 p-2 border rounded-l"
          value={input}
          onChange={e => setInput(e.target.value)}
          placeholder="Type here…"
        />
        <button className="bg-blue-500 text-white px-4 rounded-r">Send</button>
      </form>
    </div>
  );
}

/**
 * Summary screen: shows your top cards with checkboxes and a Compare button.
 */
function Summary({ cards, onRestart }) {
  const [selected, setSelected] = useState([]);

  const toggle = name => {
    setSelected(s =>
      s.includes(name) ? s.filter(n => n !== name) : [...s, name]
    );
  };

  const toCompare = cards.filter(c => selected.includes(c.name));

  return (
    <div className="h-screen max-w-xl mx-auto p-4">
      <h2 className="text-2xl mb-4">Your Top Picks</h2>
      <ul className="mb-4">
        {cards.map(c => (
          <li key={c.name} className="mb-2">
            <label className="inline-flex items-center">
              <input
                type="checkbox"
                checked={selected.includes(c.name)}
                onChange={() => toggle(c.name)}
                className="mr-2"
              />
              <span className="font-medium">{c.name}</span>{" "}
              <span className="text-sm text-gray-600">(₹{c.fee}/yr)</span>
            </label>
          </li>
        ))}
      </ul>

      {toCompare.length >= 2 ? (
        <Compare cards={toCompare.slice(0,2)} />
      ) : (
        <button
          disabled
          className="bg-gray-300 text-gray-600 px-4 py-2 rounded mb-2"
        >
          Select 2 cards to compare
        </button>
      )}

      <div className="mt-6">
        <button
          onClick={onRestart}
          className="text-blue-500 underline"
        >
          Restart Chat
        </button>
      </div>
    </div>
  );
}

/**
 * Side-by-side comparison of two cards.
 */
function Compare({ cards }) {
  const [a, b] = cards;
  return (
    <div>
      <h3 className="text-xl mb-2">Comparison</h3>
      <div className="grid grid-cols-2 gap-4">
        {[a, b].map(c => (
          <div key={c.name} className="p-4 border rounded">
            <h4 className="font-bold mb-2">{c.name}</h4>
            <p><strong>Fee:</strong> ₹{c.fee}/yr</p>
            <p><strong>Rewards:</strong> {c.rewards}</p>
            <p><strong>Perks:</strong> {c.perks}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
