import React, { useState, useEffect, useRef } from "react";

export default function App() {
  const [sessionId] = useState(() => Date.now().toString());
  const [msgs, setMsgs] = useState([
    { from: "bot", text: "Hello! I'm your Credit Card Advisor." }
  ]);
  const [input, setInput] = useState("");
  const bottomRef = useRef();

  useEffect(() => {
    bottomRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [msgs]);

  async function handleSubmit(e) {
    e.preventDefault();
    if (!input.trim()) return;

    // show user text
    setMsgs(m => [...m, { from: "user", text: input }]);
    const text = input;
    setInput("");

    // POST to backend
    const res = await fetch("http://localhost:8000/chat", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ session_id: sessionId, message: text })
    });

    if (!res.ok) {
      console.error("Server error:", res.status);
      setMsgs(m => [...m, { from: "bot", text: "Oops, server error." }]);
      return;
    }

    const { reply } = await res.json();
    setMsgs(m => [...m, { from: "bot", text: reply }]);
  }

  return (
    <div className="h-screen flex flex-col max-w-xl mx-auto p-4">
      <div className="flex-1 overflow-y-auto bg-white p-4 rounded shadow">
        {msgs.map((m,i) => (
          <div key={i} className={`my-2 ${m.from==="bot"?"text-left":"text-right"}`}>
            <span className={`inline-block p-2 rounded ${
              m.from==="bot"?"bg-gray-200":"bg-blue-500 text-white"
            }`}>
              {m.text}
            </span>
          </div>
        ))}
        <div ref={bottomRef} />
      </div>
      <form onSubmit={handleSubmit} className="mt-2 flex">
        <input
          className="flex-1 p-2 border rounded-l"
          value={input}
          onChange={e => setInput(e.target.value)}
          placeholder="Type hereâ€¦"
        />
        <button className="bg-blue-500 text-white px-4 rounded-r">
          Send
        </button>
      </form>
    </div>
  );
}
