import React, { useState, useEffect, useRef } from "react";

// A single card component used both in the chat and summary
function Card({ card, showCheckbox, checked, onToggle }) {
  return (
    <div className="border rounded-lg p-4 max-w-xs">
      {showCheckbox && (
        <input
          type="checkbox"
          className="mb-2"
          checked={checked}
          onChange={() => onToggle(card.name)}
        />
      )}
      {card.image_url && (
        <img
          src={card.image_url}
          alt={card.name}
          className="w-full h-32 object-cover rounded mb-2"
        />
      )}
      <h3 className="font-bold">{card.name}</h3>
      {card.reason && <p className="text-sm mt-1">{card.reason}</p>}
      {card.reward_simulation && (
        <p className="text-sm mt-2 italic">{card.reward_simulation}</p>
      )}
      {card.fee != null && (
        <p className="text-xs text-gray-600 mt-1">Fee: ₹{card.fee}/yr</p>
      )}
    </div>
  );
}

function App() {
  const [sessionId] = useState(() => Date.now().toString());
  const [msgs, setMsgs] = useState([
    { from: "bot", text: "Hello! I'm your Credit Card Advisor." },
  ]);
  const [input, setInput] = useState("");
  const [finished, setFinished] = useState(false);
  const [cards, setCards] = useState([]);
  const bottomRef = useRef();

  // Auto-scroll to bottom on new message
  useEffect(() => {
    bottomRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [msgs]);

  const sendMessage = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    // Echo user
    setMsgs((m) => [...m, { from: "user", text: input }]);
    const text = input;
    setInput("");

    // Call backend
    const res = await fetch("http://localhost:8000/chat", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ session_id: sessionId, message: text }),
    });

    if (!res.ok) {
      setMsgs((m) => [...m, { from: "bot", text: "Oops, something went wrong." }]);
      return;
    }

    const data = await res.json();

    // If recommendations came back, finish the chat
    if (data.recommendations && Array.isArray(data.recommendations)) {
      setCards(data.recommendations);
      setFinished(true);
    } else if (typeof data.reply === "string") {
      // Continue the Q&A
      setMsgs((m) => [...m, { from: "bot", text: data.reply }]);
    } else {
      // Unexpected shape
      setMsgs((m) => [...m, { from: "bot", text: JSON.stringify(data) }]);
    }
  };

  // SUMMARY + COMPARE SCREEN
  if (finished) {
    return <Summary cards={cards} onRestart={() => {
      setMsgs([{ from: "bot", text: "Hello! I'm your Credit Card Advisor." }]);
      setFinished(false);
      setCards([]);
      setInput("");
    }} />;
  }

  // CHAT SCREEN
  return (
    <div className="h-screen flex flex-col max-w-xl mx-auto p-4">
      <div className="flex-1 overflow-y-auto bg-white p-4 rounded shadow">
        {msgs.map((m, i) => (
          <div key={i} className={`my-2 ${m.from==="bot"?"text-left":"text-right"}`}>
            <span className={`inline-block p-2 rounded whitespace-pre-wrap ${
                m.from==="bot" ? "bg-gray-200" : "bg-blue-500 text-white"
              }`}>
              {m.text}
            </span>
          </div>
        ))}
        <div ref={bottomRef} />
      </div>
      <form onSubmit={sendMessage} className="mt-2 flex">
        <input
          className="flex-1 p-2 border rounded-l"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Type your answer here…"
        />
        <button className="bg-blue-500 text-white px-4 rounded-r">Send</button>
      </form>
    </div>
  );
}

// SUMMARY view with checkbox selection + compare
function Summary({ cards, onRestart }) {
  const [selected, setSelected] = useState([]);

  const toggle = (name) => {
    setSelected((s) =>
      s.includes(name) ? s.filter((n) => n !== name) : [...s, name]
    );
  };

  const toCompare = cards.filter((c) => selected.includes(c.name));

  return (
    <div className="h-screen max-w-xl mx-auto p-4">
      <h2 className="text-2xl mb-4">Your Top Picks</h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 gap-4 mb-4">
        {cards.map((c) => (
          <Card
            key={c.name}
            card={c}
            showCheckbox
            checked={selected.includes(c.name)}
            onToggle={toggle}
          />
        ))}
      </div>

      {toCompare.length >= 2 ? (
        <Compare cards={toCompare.slice(0, 2)} />
      ) : (
        <button
          disabled
          className="bg-gray-300 text-gray-600 px-4 py-2 rounded mb-2"
        >
          Select 2 cards to compare
        </button>
      )}

      <div className="mt-6">
        <button onClick={onRestart} className="text-blue-500 underline">
          Restart Chat
        </button>
      </div>
    </div>
  );
}

// Side-by-side comparison of exactly two cards
function Compare({ cards }) {
  const [a, b] = cards;
  return (
    <div>
      <h3 className="text-xl mb-2">Comparison</h3>
      <div className="grid grid-cols-2 gap-4">
        {[a, b].map((c) => (
          <div key={c.name} className="p-4 border rounded">
            <h4 className="font-bold mb-2">{c.name}</h4>
            {c.fee != null && <p><strong>Fee:</strong> ₹{c.fee}/yr</p>}
            {c.reason && <p><strong>Reason:</strong> {c.reason}</p>}
            {c.reward_simulation && (
              <p><strong>Reward Sim.:</strong> {c.reward_simulation}</p>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
