import React, { useState } from 'react';

// A simple Card component
function Card({ card }) {
  return (
    <div className="border rounded-lg p-4 max-w-xs">
      <img src={card.image_url} alt={card.name} className="w-full h-32 object-cover rounded" />
      <h3 className="mt-2 font-bold">{card.name}</h3>
      <p className="text-sm mt-1">{card.reason}</p>
      <p className="text-sm mt-2 italic">{card.reward_simulation}</p>
    </div>
  );
}

function App() {
  const [message, setMessage] = useState('');
  const [chat, setChat] = useState([]);

  const sendMessage = async () => {
    if (!message) return;

    // Add user bubble
    setChat(chat => [...chat, { sender: 'user', text: message }]);
    setMessage('');

    // Call backend
    const res = await fetch('http://localhost:8000/chat', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ message })
    });
    const { reply } = await res.json();

    let parsed;
    try {
      parsed = JSON.parse(reply);
    } catch {
      // Not JSON → just a text reply
      setChat(chat => [...chat, { sender: 'bot', text: reply }]);
      return;
    }

    // If it has recommendations, render specially
    if (parsed.recommendations && Array.isArray(parsed.recommendations)) {
      setChat(chat => [...chat, { sender: 'bot', cards: parsed.recommendations }]);
    } else {
      // fallback
      setChat(chat => [...chat, { sender: 'bot', text: reply }]);
    }
  };

  return (
    <div className="flex flex-col items-center p-4">
      <h1 className="text-2xl mb-4">Credit Card Advisor</h1>

      <div className="w-full max-w-3xl border p-4 h-[500px] overflow-y-auto mb-4 space-y-4">
        {chat.map((msg, idx) => (
          <div key={idx}>
            {msg.sender === 'user' ? (
              <div className="text-right">
                <p className="inline-block bg-blue-100 p-2 rounded">{msg.text}</p>
              </div>
            ) : msg.cards ? (
              // Render the recommendations grid
              <div>
                <p className="font-semibold mb-2">Here are my top picks:</p>
                <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4">
                  {msg.cards.map((card, i) => (
                    <Card key={i} card={card} />
                  ))}
                </div>
                {/* Compare button toggles in-chat compare view */}
                <button
                  className="mt-4 px-4 py-2 bg-green-500 text-white rounded"
                  onClick={() => {
                    setChat(chat => [
                      ...chat,
                      { sender: 'bot', compare: msg.cards }
                    ]);
                  }}
                >
                  Compare Cards
                </button>
              </div>
            ) : msg.compare ? (
              // Inline compare table
              <div>
                <p className="font-semibold mb-2">Comparison:</p>
                <table className="w-full table-auto border-collapse">
                  <thead>
                    <tr>
                      <th className="border p-2">Card</th>
                      <th className="border p-2">Annual Fee</th>
                      <th className="border p-2">Reward Rate</th>
                      <th className="border p-2">Perk</th>
                    </tr>
                  </thead>
                  <tbody>
                    {msg.compare.map((c, i) => (
                      <tr key={i}>
                        <td className="border p-2">{c.name}</td>
                        <td className="border p-2">{c.annual_fee || 'N/A'}</td>
                        <td className="border p-2">{c.reward_rate || 'N/A'}</td>
                        <td className="border p-2">{c.reason}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            ) : (
              // Plain text bot bubble
              <div className="text-left">
                <p className="inline-block bg-gray-200 p-2 rounded">{msg.text}</p>
              </div>
            )}
          </div>
        ))}
      </div>

      <div className="flex w-full max-w-3xl">
        <input
          className="flex-grow border p-2"
          value={message}
          onChange={e => setMessage(e.target.value)}
          onKeyDown={e => e.key === 'Enter' && sendMessage()}
        />
        <button
          className="ml-2 p-2 bg-blue-500 text-white rounded"
          onClick={sendMessage}
        >
          Send
        </button>
      </div>
    </div>
  );
}

export default App;
